import React, { useState, useEffect } from 'react';
import { BarChart, CartesianGrid, XAxis, YAxis, Tooltip, Legend, Bar } from 'recharts';
import _ from 'lodash';

const Statistics = () => {
  const GET_ALL_URL = 'https://traineeapp.azurewebsites.net/gettrainings';
  const [trainings, setTrainings] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(GET_ALL_URL);
        if (!response.ok) {
          throw new Error(`Error fetching trainings: ${response.status}`);
        }
        const data = await response.json();
        setTrainings(data);
      } catch (error) {
        console.error('Error fetching trainings:', error);
      }
    };

    fetchData();
  }, []);

  const data = _(trainings)
    .groupBy('activity')
    .map((group, name) => ({
      name,
      minutes: _.sumBy(group, 'duration'),
    }))
    .value();

  return (
    <div>
      <h2>Activity Statistics</h2>
      <BarChart width={600} height={300} data={data}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Bar dataKey="minutes" fill="#8884d8" />
      </BarChart>
    </div>
  );
};

export default Statistics;
